DROP TABLE IF EXISTS bicycle_thefts;
CREATE TABLE bicycle_thefts (
  voorval_nummer character varying,
  kennisname character varying,
  mk character varying,
  mk_omschrijving character varying,
  poging character varying,
  district character varying,
  werkgebied character varying,
  plaats character varying,
  buurt character varying,
  straat character varying,
  begin_dagsoort character varying,
  begindatum character varying,
  begintijd character varying,
  eind_dagsoort character varying,
  einddatum character varying,
  eindtijd character varying,
  gemiddelde_jaar character varying,
  gemiddelde_maand character varying,
  gemiddelde_dagsoort character varying,
  gemiddelde_dagdeel character varying,
  trefwoord character varying,
  object character varying,
  merk character varying,
  type character varying,
  kleur character varying
);


/* Hoeveel fietsen worden met geweld gestolen? */
SELECT COUNT(*) FROM bicycle_thefts WHERE mk_omschrijving LIKE 'DIEFSTAL MET GEWELD%';

/* Hoeveel fietsdiefstallen zijn er getelt per soort diefstal? */
SELECT COUNT(*), mk_omschrijving FROM bicycle_thefts WHERE plaats = 'ROTTERDAM' GROUP BY mk_omschrijving;

/* Op welke datum zijn de meeste fietsen gestolen? */
SELECT COUNT(*) AS count, kennisname FROM bicycle_thefts GROUP BY kennisname ORDER BY count DESC;

/* Om welke tijd worden de meeste fietsen gestolen? */
SELECT COUNT(*) AS count, begintijd FROM bicycle_thefts GROUP BY begintijd ORDER BY count DESC;